<div class="block full">    <div class="block-title">        <h2>News Table</h2>    </div>    <?php if(isset($message)){ ?>        <div class="alert alert-success alert-dismissable">            <?php            echo $message;            ?>        </div>        <?php    }?>    <div class="table-responsive">        <table id="example-datatable" class="table table-bordered table-vcenter">            <thead>            <tr>                <th class="text-center" style="width:100px;">Serial No</th>                <th>News Title</th>                <th>News Date</th>                <th class="text-center">Status</th>                <th style="width: 125px;" class="text-center"><i class="fa fa-flash"></i></th>            </tr>            </thead>            <tbody>            <?php            foreach ($query as $k=>$row)            {?>                <tr id="<?php echo $row->id;?>">                    <td class="text-center"><?php echo $k+1;?></td>                    <td><?php echo $row->title;?></td>                    <td><?php echo $row->news_date;?></td>                    <?php if ($row->status==1){?>                    <td class="hidden-sm hidden-xs text-center"><a href="javascript:void(0)" class="label label-success">Enabled</a></td><?php } else {?>                    <td class="hidden-sm hidden-xs text-center"><a href="javascript:void(0)" class="label label-danger">Disabled</a></td><?php } ?>                    <td class="text-center">                        <a href="#modal-fade" data-toggle="modal" title="view_news" class="btn btn-effect-ripple btn-xs btn-info view-modal" data-id="<?php echo $row->id;?>"  data-title="<?php echo $row->title;?>"                           data-short_description="<?php echo $row->short_description;?>"                           data-content="<?php echo $row->content;?>"                           data-news-date="<?php echo $row->news_date;?>"                           data-status="<?php echo $row->status;?>"><i class="fa fa-eye"></i></a>                        <a href="<?php echo base_url('admin/news/edit/'.$row->id);?>" data-toggle="tooltip" title="edit_news" class="btn btn-effect-ripple btn-xs btn-success"><i class="fa fa-pencil"></i></a>                        <a href="#" data-href="<?php echo base_url('delete/delete_news?id='.$row->id);?>" data-toggle="modal" title="delete_news" data-title="<?php echo $row->title;?>" data-target="#confirm-delete" class="btn btn-effect-ripple btn-xs btn-danger del-row"><i class="fa fa-times"></i>                        </a>                    </td>                </tr>            <?php } ?>    </tbody>    </table></div></div><div id="modal-fade" class="modal fade" tabindex="-1" role="dialog" aria-hidden="true">    <div class="modal-dialog">        <div class="modal-content">            <div class="modal-header">                <button type="button" class="close" data-dismiss="modal" aria-hidden="true">&times;</button>                <h3 class="modal-title"><strong>News Details</strong></h3>            </div>            <div class="modal-body">                <div class="box span3">                    <div class="box-content">                        <table class="table table-bordered">                            <tbody>                            <tr>                                <td>ID</td>                                <td id="news_id"></td>                            </tr>                            <tr>                                <td>News Title</td>                                <td id="news_title"></td>                            </tr>                            <tr>                                <td>Short Description</td>                                <td id="short_description"></td>                            </tr>                            <tr>                                <td>Content</td>                                <td id="content"></td>                            </tr>                            <tr>                                <td>News Date</td>                                <td id="news_date"></td>                            </tr>                            <tr>                                <td>Status</td>                                <td id="status"></td>                            </tr>                            </tbody>                        </table>                    </div>                </div>            </div>            <div class="modal-footer">                <button type="button" class="btn btn-effect-ripple btn-danger" data-dismiss="modal">Close</button>            </div>        </div>    </div></div><div id="confirm-delete" class="modal fade" tabindex="-1" role="dialog" aria-hidden="true">    <div class="modal-dialog modal-sm">        <div class="modal-content">            <div class="modal-header">                <button type="button" class="close" data-dismiss="modal" aria-hidden="true">&times;</button>                <h4 class="modal-title"><span id="del_name"></span></h4>            </div>            <div class="modal-body">                Are you sure you want to delete this record?            </div>            <div class="modal-footer">                <a class="btn btn-effect-ripple btn-danger">Delete</a>                <button type="button"  data-dismiss="modal"class="btn btn-effect-ripple btn-default" data-dismiss="modal">Cancel</button>            </div>        </div>    </div></div><script type="text/javascript">    $(function(){        $('a.view-modal').click(function(e){            var id = $(this).attr('data-id');            var title = $(this).attr('data-title');            var content = $(this).attr('data-content');            var short_description = $(this).attr('data-short-description');            var news_date = $(this).attr('data-news-date');            var status = $(this).attr('data-status');            $('td#news_id').html(id);            $('td#news_title').html(title);            $('td#short_description').html(short_description);            $('td#content').html(content);            $('td#news_date').html(news_date);            if(status==1)            {                $('td#status').text("Enabled");            }else            {                $('td#status').text("Disabled");            }        })    });    $(function(){        $('a.del-row').click(function(e) {            var name = $(this).attr('data-title');            $('span#del_name').html(name);        });    });    $('#confirm-delete').on('show.bs.modal', function(e) {        $(this).find('.btn-danger').attr('href', $(e.relatedTarget).data('href'));    });    $(function() {        var fixHelper = function(e, ui) {            ui.children().each(function() {                $(this).width($(this).width());            });            return ui;        };        $("#example-datatable tbody").sortable({            update: function(event, ui){                var order = $(this).sortable("serialize");                console.log(order);            },            helper: fixHelper,            stop : function(e,ui){                var sort = [];                for(var i = 0; i < $('tbody.ui-sortable tr').length; i++){                    var sel = $('tbody.ui-sortable tr')[i];                    sort.push({                        id : $(sel).attr('id'),                        position : i                    });                }                $.ajax({                    url : '<?php echo base_url('admin/update-order/news');?>',                    type :'POST',                    data : JSON.stringify(sort),                    dataType : 'JSON',                    success : function(data){                    },                    error : function(data){                    }                });            }        }).disableSelection();    });</script><script src="<?php echo base_url('public/js/pages/uiTables.js');?>"></script><script>$(function(){ UiTables.init(); });</script>